mapper.update:
UPDATE 	FJ_RECRUIT 
SET 	readcount = readcount + 1 
Where	recruit_id = #{recruit_id}

mapper.select:
SELECT 	* 
From	FJ_USER
Where	#{min_salary} >= want_salary or want_salary <= #{max_salary}

